package com.guofei.mvc.listener;import com.guofei.mvc.event.UserRegisterEvent;import lombok.extern.slf4j.Slf4j;import org.springframework.context.event.EventListener;import org.springframework.stereotype.Service;/** * @author guofei * @date 2022/4/21 6:25 PM */@Service@Slf4jpublic class CouponService {  @EventListener  public void addCoupon(UserRegisterEvent userRegisterEvent){    log.info("给用户[{}]发放优惠券[{}]", userRegisterEvent.getUsername(),"test");  }}